import React from 'react';
import { addMovie, removeMovie, fetchMovie, hideButton, showButton } from '../Redux/actions/index';
import { connect } from 'react-redux';
import MovieItem from './MovieItem';
import './../App.css';

class MovieList extends React.Component {

    componentDidMount() {
        this.props.fetchMovie()
    }

    handleMouseEnter(s){

        this.props.showbutton(s);
        console.log(this.props[s])
    }

    handleMouseLeave (s) {

        this.props.hidebutton(s);
        console.log(this.props[s])
    }

    render() {
        const myList = this.props.MovieList["mylist"];
        const recommendList = this.props.MovieList["recommendations"];
        return (
            <div >
                <h2 className="title">My List</h2>
                <ul className="movie-content" >
                {
                    myList.map((item) => {
                    return  <li key={item.id}>

                                <div
                onMouseEnter={()=>this.handleMouseEnter(item.title)} 
                onMouseLeave={()=>this.handleMouseLeave(item.title)}
                className="movieItem"
            >
                <img src={item.img} alt={item.title} width="100%" height="250px"/>
                <p className="movieItem-title"> {item.title} </p>
                <div className='button'>
                    <button 
                        className={[item.title] ? "button-show":"button-hidden"}
                        onClick={()=>this.props.removeMovie(item)}> {this.props.button} 
                    </button>
                </div>
            </div>
                            </li>
                    })
                } 
                </ul>
                <h2 className="title">Recommendation List</h2>
                <ul className="movie-content" >
                {
                    recommendList.map((item) => {
                    return  <li key={item.id}>
                                <MovieItem item = {item}  button="Add"  onClick={()=>this.props.addMovie(item)}/>                     
                            </li>
                    })
                } 
                </ul>
            </div>

        )
    }
}

const mapStateToProps = (state, props) => {
    return {
        MovieList: state
    }
}

const mapDispatchToProps = (dispatch) => ({
    fetchMovie: () => { dispatch(fetchMovie())},
    addMovie: (movie) => { 
        dispatch(addMovie(movie));
        dispatch(hideButton(movie.title))
    },
    removeMovie: (movie) => { dispatch(removeMovie(movie));
        dispatch(hideButton(movie.title))
    },
    showbutton: (s) => { dispatch(showButton(s))},
    hidebutton: (s) => { dispatch(hideButton(s))}
})


export default connect(mapStateToProps, mapDispatchToProps)(MovieList)
